import React, { useState } from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import chevronRight from '../assets/img/chevron-right.svg';
import chevronLeft from '../assets/img/chevron-left.svg';
import '../assets/css/LogementCarousel.scss';

const LogementCarousel = ({ pictures }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const handleSlideChange = (index) => {
    setCurrentSlide(index);
  };

  const showArrowsAndPagination = pictures.length > 1;

  return (
    <div className="logement-carousel">
      <Carousel
        showThumbs={false}
        dynamicHeight={true}
        showStatus={false}
        renderIndicator={() => null}
        selectedItem={currentSlide}
        onChange={handleSlideChange}
        renderArrowPrev={(onClickHandler, hasPrev, label) =>
          showArrowsAndPagination && hasPrev && (
            <button type="button" onClick={onClickHandler} title={label} className="arrow arrow-prev">
              <img src={chevronLeft} alt="Précédent" />
            </button>
          )
        }
        renderArrowNext={(onClickHandler, hasNext, label) =>
          showArrowsAndPagination && hasNext && (
            <button type="button" onClick={onClickHandler} title={label} className="arrow arrow-next">
              <img src={chevronRight} alt="Suivant" />
            </button>
          )
        }
      >
        {pictures.map((picture, index) => (
          <div key={index}>
            <img src={picture} alt={`Logement ${index + 1}`} />
          </div>
        ))}
      </Carousel>
      {showArrowsAndPagination && (
        <div className="pagination">
          {currentSlide + 1}/{pictures.length}
        </div>
      )}
    </div>
  );
};

export default LogementCarousel;
