import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Helmet, HelmetProvider } from 'react-helmet-async';
import logements from '../datas/logements.json';
import LogementCarousel from '../components/LogementCarousel';
import '../assets/css/Logement.scss';

import star from '../assets/img/star.svg';

const Logement = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [logement, setLogement] = useState(null);
  const [expandedSection, setExpandedSection] = useState(null);

  useEffect(() => {
    const foundLogement = logements.find((logement) => logement.id === id);
    if (foundLogement) {
      setLogement(foundLogement);
    } else {
      navigate('/404', { replace: true });
    }
  }, [id, navigate]);

  if (!logement) {
    return null;
  }

  const toggleSection = (section) => {
    setExpandedSection(expandedSection === section ? null : section);
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, i) => (
      <img
        key={i}
        src={star}
        alt={`${i + 1} étoile`}
        className={i < rating ? 'star-filled' : 'star-empty'}
      />
    ));
  };

  return (
    <HelmetProvider>
      <div className="logement2-container">
        <Helmet>
          <title>Kasa FR | {logement.title}</title>
        </Helmet>
        <LogementCarousel pictures={logement.pictures} />
        <div className="logement2-details">
          <div className="logement2-header">
            <div className="logement2-title-host">
              <div className="logement2-title">
                <h1>{logement.title}</h1>
                <p>{logement.location}</p>
              </div>
              <div className="logement2-host">
                <p>{logement.host.name}</p>
                <img src={logement.host.picture} alt={logement.host.name} className="host-picture" />
              </div>
            </div>
            <div className="logement2-tags-rating">
              <div className="logement2-tags">
                {logement.tags.map((tag, index) => (
                  <span key={index} className="tag">
                    {tag}
                  </span>
                ))}
              </div>
              <div className="logement2-rating">{renderStars(logement.rating)}</div>
            </div>
          </div>
          <div className="logement2-expandable">
            <button onClick={() => toggleSection('description')}>Description</button>
            {expandedSection === 'description' && <p>{logement.description}</p>}
            <button onClick={() => toggleSection('equipements')}>Équipements</button>
            {expandedSection === 'equipements' && (
              <ul>
                {logement.equipments.map((equipment, index) => (
                  <li key={index}>{equipment}</li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>
    </HelmetProvider>
  );
};

export default Logement;
