import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Helmet, HelmetProvider } from 'react-helmet-async';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import logements from '../datas/logements.json';
import chevronRight from '../assets/img/chevron-right.svg';
import chevronLeft from '../assets/img/chevron-left.svg';
import '../assets/css/Logement.scss';

const Logement = () => {
  const { id } = useParams();
  const [logement, setLogement] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const foundLogement = logements.find(logement => logement.id === id);
    setLogement(foundLogement);
  }, [id]);

  if (!logement) {
    return <div>Logement non trouvé</div>;
  }

  const handleSlideChange = (index) => {
    setCurrentSlide(index);
  };

  return (
    <HelmetProvider>
      <div>
        <Helmet>
          <title>Kasa FR | Logement {logement.title}</title>
        </Helmet>
        <section className="logement-carousel">
          <Carousel
            showThumbs={false}
            dynamicHeight={true}
            showStatus={false}
            renderIndicator={() => null}
            selectedItem={currentSlide}
            onChange={handleSlideChange}
            renderArrowPrev={(onClickHandler, hasPrev, label) =>
              hasPrev && (
                <button type="button" onClick={onClickHandler} title={label} className="arrow arrow-prev">
                  <img src={chevronLeft} alt="Précédent" />
                </button>
              )
            }
            renderArrowNext={(onClickHandler, hasNext, label) =>
              hasNext && (
                <button type="button" onClick={onClickHandler} title={label} className="arrow arrow-next">
                  <img src={chevronRight} alt="Suivant" />
                </button>
              )
            }
          >
            {logement.pictures.map((picture, index) => (
              <div key={index}>
                <img src={picture} alt={`Logement ${index + 1}`} />
              </div>
            ))}
          </Carousel>
          <div className="pagination">
            {currentSlide + 1}/{logement.pictures.length}
          </div>
          <div className="logement-details">
            <h1>{logement.title}</h1>
            <p>{logement.description}</p>
            {/* Ajoutez d'autres détails du logement ici */}
          </div>
        </section>
      </div>
    </HelmetProvider>
  );
};

export default Logement;
