import React, { useState } from 'react';
import { Helmet, HelmetProvider } from 'react-helmet-async';
import Banner from '../components/Banner';
import bannerImage from '../assets/img/banner2-min.webp';
import '../assets/css/About.scss';

const About = () => {
  const [openSection, setOpenSection] = useState(null);

  const toggleSection = (index) => {
    setOpenSection(openSection === index ? null : index);
  };

  const sections = [
    {
      title: 'Fiabilité',
      content: 'Les annonces postées sur Kasa garantissent une fiabilité totale. Les photos sont conformes aux logements, et toutes les informations sont régulièrement vérifiées par nos équipes.',
    },
    {
      title: 'Respect',
      content: 'Le respect fait partie des valeurs fondamentales de Kasa. Tout comportement discriminatoire, raciste ou sexiste entraînera une exclusion de notre plateforme.',
    },
    {
      title: 'Service',
      content: 'Nos équipes se tiennent à votre disposition pour vous fournir une expérience parfaite. N\'hésitez pas à nous contacter si vous avez la moindre question.',
    },
    {
      title: 'Sécurité',
      content: 'La sécurité est la priorité de Kasa. Aussi bien pour nos hôtes que pour les voyageurs, chaque logement respecte les critères de sécurité établis par nos services.',
    },
  ];

  return (
    <HelmetProvider>
      <div>
        <Helmet>
          <title>Kasa FR | À Propos</title>
        </Helmet>
        <Banner image={bannerImage} />
        <div className="about-sections">
          {sections.map((section, index) => (
            <div key={index} className="about-section">
              <div className="section-header" onClick={() => toggleSection(index)}>
                <h2>{section.title}</h2>
                <img
                  src={openSection === index ? '../assets/img/chevron-up.svg' : '../assets/img/chevron-down.svg'}
                  alt="Toggle section"
                />
              </div>
              {openSection === index && <div className="section-content">{section.content}</div>}
            </div>
          ))}
        </div>
      </div>
    </HelmetProvider>
  );
};

export default About;
