import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Helmet, HelmetProvider } from 'react-helmet-async';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import logements from '../datas/logements.json';
import '../assets/css/Logement.scss'; // Assurez-vous d'avoir un fichier CSS pour le style spécifique de la page Logement

const Logement = () => {
  const { id } = useParams();
  const [logement, setLogement] = useState(null);

  useEffect(() => {
    const foundLogement = logements.find(logement => logement.id === id);
    setLogement(foundLogement);
  }, [id]);

  if (!logement) {
    return <div>Logement non trouvé</div>;
  }

  return (
    <HelmetProvider>
      <div>
        <Helmet>
          <title>Kasa FR | Logement {logement.title}</title>
        </Helmet>
        <section className="logement-carousel">
          <Carousel showThumbs={false} dynamicHeight={true}>
            {logement.pictures.map((picture, index) => (
              <div key={index}>
                <img src={picture} alt={`Logement ${index + 1}`} />
              </div>
            ))}
          </Carousel>
          <div className="logement-details">
            <h1>{logement.title}</h1>
            <p>{logement.description}</p>
            {/* Ajoutez d'autres détails du logement ici */}
          </div>
        </section>
      </div>
    </HelmetProvider>
  );
};

export default Logement;
